template.template_md:
{% block content %}
<style>
  @page {
    margin: 0.5in;
    size: letter landscape;
    /* or A4 landscape */
  }

  /* —— Only per‑report row & badge colors —— */
  .status-healthy {
    background-color: var(--status-ready-bg);
  }

  .status-warning {
    background-color: var(--status-not-ready-bg);
  }

  .status-critical {
    background-color: var(--status-unknown-bg);
  }

  .status-unknown {
    background-color: #f8f9fa;
  }

  .badge-healthy {
    background-color: var(--status-ready-badge);
  }

  .badge-warning {
    background-color: var(--status-not-ready-badge);
  }

  .badge-critical {
    background-color: var(--status-unknown-badge);
    color: #333;
  }

  .badge-unknown {
    background-color: #6c757d;
    color: white;
  }

  .protection-enabled {
    background-color: var(--status-ready-badge);
    color: white;
  }

  .protection-disabled {
    background-color: var(--status-not-ready-badge);
    color: white;
  }
</style>

<div class="report-header">
  <div class="header-logo">
    <img src="https://github.com/amidaware/reporting-templates/blob/master/assets/gbtnavy%20(256).png?raw=true" alt="Amidaware Logo" style="width: 80px; height: auto;">
  </div>
  <div class="header-titles">
    <h1>Trend Micro WFBS Status Report</h1>
    <h2>Client: {{ client.name }}</h2>
  </div>
  <div class="header-date">
    <h2>Report Date:<br>{{ report_run_timestamp.strftime('%B %d, %Y') }}</h2>
  </div>
</div>

<div class="section">
  <table class="report-table">
    <thead>
      <tr>
        <th>Client</th>
        <th>Site</th>
        <th>Device Name</th>
        <th>Operating System</th>
        <th style="text-align: center;">TM Status</th>
        <th style="text-align: center;">Installed</th>
        <th style="text-align: center;">Service</th>
        <th style="text-align: center;">Signature Age</th>
        <th style="text-align: center;">Last Update</th>
      </tr>
    </thead>
    <tbody>
      {% set healthy_devices, warning_devices, critical_devices, unknown_devices = [], [], [], [] %}

      {% for agent in data_sources.trend_micro_data %}
      {% if agent.custom_fields.trend_micro_status %}
        {% if '"health_status":"OK"' in agent.custom_fields.trend_micro_status %}
          {% set _ = healthy_devices.append(agent) %}
        {% elif '"health_status":"REALTIME_DISABLED"' in agent.custom_fields.trend_micro_status %}
          {% set _ = healthy_devices.append(agent) %}
        {% elif '"health_status":"UNKNOWN"' in agent.custom_fields.trend_micro_status %}
          {% set _ = unknown_devices.append(agent) %}
        {% else %}
          {% set _ = critical_devices.append(agent) %}
        {% endif %}
      {% else %}
        {% set _ = unknown_devices.append(agent) %}
      {% endif %}
      {% endfor %}

      {% set sorted_devices = (critical_devices | sort(attribute='hostname') | sort(attribute='site__name')) +
                              (warning_devices | sort(attribute='hostname') | sort(attribute='site__name')) +
                              (unknown_devices | sort(attribute='hostname') | sort(attribute='site__name')) +
                              (healthy_devices | sort(attribute='hostname') | sort(attribute='site__name')) %}

      {% for agent in sorted_devices %}
      {% set row_class = 'status-unknown' %}
      {% set badge_class = 'badge-unknown' %}
      {% set status_text = 'No Data' %}
      {% set installed_status = 'N/A' %}
      {% set service_status = 'N/A' %}
      {% set protection_status = 'N/A' %}
      {% set protection_class = 'badge-unknown' %}
      {% set signature_age = 'N/A' %}
      {% set last_update = 'N/A' %}

      {% if agent.custom_fields.trend_micro_status %}
        {% if '"health_status":"OK"' in agent.custom_fields.trend_micro_status %}
          {% set row_class = 'status-healthy' %}
          {% set badge_class = 'badge-healthy' %}
          {% set status_text = 'Healthy' %}
        {% elif '"health_status":"REALTIME_DISABLED"' in agent.custom_fields.trend_micro_status %}
          {% set row_class = 'status-healthy' %}
          {% set badge_class = 'badge-healthy' %}
          {% set status_text = 'Healthy' %}
        {% elif '"health_status":"UNKNOWN"' in agent.custom_fields.trend_micro_status %}
          {% set row_class = 'status-unknown' %}
          {% set badge_class = 'badge-unknown' %}
          {% set status_text = 'Not Installed' %}
        {% else %}
          {% set row_class = 'status-critical' %}
          {% set badge_class = 'badge-critical' %}
          {% set status_text = 'Critical' %}
        {% endif %}

        {% if '"installed":1' in agent.custom_fields.trend_micro_status %}
          {% set installed_status = '✅' %}
        {% else %}
          {% set installed_status = '❌' %}
        {% endif %}

        {% if '"service_running":1' in agent.custom_fields.trend_micro_status %}
          {% set service_status = '✅' %}
        {% else %}
          {% set service_status = '❌' %}
        {% endif %}

        {% if '"realtime_protection":1' in agent.custom_fields.trend_micro_status %}
          {% set protection_status = 'Enabled' %}
          {% set protection_class = 'protection-enabled' %}
        {% else %}
          {% set protection_status = 'Disabled' %}
          {% set protection_class = 'protection-disabled' %}
        {% endif %}

        {% if '"signature_age":0.' in agent.custom_fields.trend_micro_status %}
          {% set signature_age = '< 1 day' %}
        {% elif '"signature_age":1.' in agent.custom_fields.trend_micro_status %}
          {% set signature_age = '~1 day' %}
        {% elif '"signature_age":-1' in agent.custom_fields.trend_micro_status %}
          {% set signature_age = 'Unknown' %}
        {% else %}
          {% set signature_age = '> 1 day' %}
        {% endif %}

        {% if '"last_update":"2025-07-28"' in agent.custom_fields.trend_micro_status %}
          {% set last_update = 'Today' %}
        {% elif '"last_update":"2025-07-27"' in agent.custom_fields.trend_micro_status %}
          {% set last_update = 'Yesterday' %}
        {% elif '"last_update":"Unknown"' in agent.custom_fields.trend_micro_status %}
          {% set last_update = 'Unknown' %}
        {% else %}
          {% set last_update = 'Outdated' %}
        {% endif %}
      {% endif %}

      <tr class="{{ row_class }}">
        <td>{{ agent.site__client__name or 'N/A' }}</td>
        <td>{{ agent.site__name or 'N/A' }}</td>
        <td>
          <b>{{ agent.hostname or 'N/A' }}</b><br>
          {% if agent.last_seen %}
          <small {% if (report_run_timestamp - agent.last_seen).days > 30 %} class="text-danger" {% endif %}>
              Last Seen: {{ agent.last_seen.astimezone(ZoneInfo('America/New_York')).strftime('%Y-%m-%d %H:%M:%S') }}
            </small>
          {% else %}
          <small>N/A</small>
          {% endif %}
        </td>
        <td style="word-wrap: break-word;">{{ agent.operating_system or 'N/A' }}</td>
        <td style="text-align: center;">
          <span class="status-badge {{ badge_class }}">{{ status_text }}</span>
        </td>
        <td style="text-align: center;">{{ installed_status }}</td>
        <td style="text-align: center;">{{ service_status }}</td>
        <td style="text-align: center;">{{ signature_age }}</td>
        <td style="text-align: center;">{{ last_update }}</td>
      </tr>
      {% endfor %}
    </tbody>
  </table>
</div>

<div class="section">
  <h3>Summary Statistics</h3>
  {% set healthy_count = healthy_devices|length %}
  {% set warning_count = warning_devices|length %}
  {% set critical_count = critical_devices|length %}
  {% set unknown_count = unknown_devices|length %}
  {% set total_count = data_sources.trend_micro_data|length %}

  <div style="display: flex; gap: 20px; margin: 20px 0;">
    <div style="padding: 15px; border-left: 4px solid var(--status-ready-badge); background: var(--status-ready-bg);">
      <h4 style="margin: 0; color: var(--status-ready-badge);">✅ Healthy</h4>
      <p style="margin: 5px 0 0 0; font-size: 24px; font-weight: bold;">{{ healthy_count }}</p>
      <small>{{ "%.1f"|format((healthy_count / total_count * 100) if total_count > 0 else 0) }}%</small>
    </div>
    <div style="padding: 15px; border-left: 4px solid var(--status-not-ready-badge); background: var(--status-not-ready-bg);">
      <h4 style="margin: 0; color: var(--status-not-ready-badge);">⚠️ Warning</h4>
      <p style="margin: 5px 0 0 0; font-size: 24px; font-weight: bold;">{{ warning_count }}</p>
      <small>{{ "%.1f"|format((warning_count / total_count * 100) if total_count > 0 else 0) }}%</small>
    </div>
    <div style="padding: 15px; border-left: 4px solid var(--status-unknown-badge); background: var(--status-unknown-bg);">
      <h4 style="margin: 0; color: var(--status-unknown-badge);">❌ Critical</h4>
      <p style="margin: 5px 0 0 0; font-size: 24px; font-weight: bold;">{{ critical_count }}</p>
      <small>{{ "%.1f"|format((critical_count / total_count * 100) if total_count > 0 else 0) }}%</small>
    </div>
    <div style="padding: 15px; border-left: 4px solid #6c757d; background: #f8f9fa;">
      <h4 style="margin: 0; color: #6c757d;">❓ Unknown</h4>
      <p style="margin: 5px 0 0 0; font-size: 24px; font-weight: bold;">{{ unknown_count }}</p>
      <small>{{ "%.1f"|format((unknown_count / total_count * 100) if total_count > 0 else 0) }}%</small>
    </div>
  </div>
</div>

{% endblock %}
